var thumbnail_mode = "no-float" ;
    summary_noimg = 300; /* طول الملخص للمواضيع بدون صورة */
    summary_img = 290; /* طول الملخص للمواضيع ذات صورة*/
    img_thumb_height = 250; /* عرض الصورة في الملخص*/
    img_thumb_width = 590; /*طول الصورة في الملخص */


